import React, { useEffect, useState } from "react";

import { useNavigate } from "react-router-dom";
//import Tilt from 'react-parallax-tilt';

function AdminLogin() {



  const navigate = useNavigate();

  const [errEmail, setErrEmail] = useState("");
  const [errPass, setErrPass] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmail = () => {
    if (email === "" || email === " ") {
      setErrEmail("Email cannot be empty");
      return false;
    } else if (!email.match(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/)) {
      setErrEmail("Enter a proper email");
      return false;
    } else {
      setErrEmail("");
      return true;
    }
  };
  useEffect(()=>{
    const userInfo =localStorage.getItem("adminInfo");
    if(userInfo){
      navigate('/admin/home')
    }
  })


  const handlePass = () => {
    if (password === "" || password === "  ") {
      setErrPass("password cannot be empty");
      return false;
    } else if (password.length < 3) {
      setErrPass("password must be 3 letters");
      return false;
    } else {
      setErrPass("");
      return true;
    }
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (!handlePass() && !handleEmail()) {
    } else {
      console.log("login success");

      const admin = {
        email: "admin@gmail.com",
        password: "123",
      };
      if (email == admin.email && password == admin.password) {
        localStorage.setItem("adminInfo", JSON.stringify(admin));
        navigate("/admin/home");
      } else if (email != admin.email) {
        setErrEmail("Email is invalid");
      } else if (password != admin.password) {
        setErrPass("Password is invalid");
      }
    }
    // let email = e.target.elements.email?.value;
    // let password = e.target.elements.password?.value;

    console.log(email, password);
  };
  return (
    <div className="App bg-lime-00 h-screen w-screen relative overflow-hidden flex justify-center items-center">
      {/* <div className="h-40-r w-40-r bg-gradient-to-r from-green-400 to-blue-500 rounded-full absolute left-2/3 -top-56 transform rotate-160 animate-pulse"></div>
      <div className="h-35-r w-35-r bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 rounded-full absolute top-96 -left-20 transform rotate-180 animate-pulse"></div>
       */}
        <div className="container h-96 w-96 bg-white bg-opacity-10 rounded-2xl shadow-5xl relative z-2 border border-opacity-30 border-r-0 border-b-0 backdrop-filter backdrop-blur-sm">
          <form className='h-full flex flex-col justify-evenly items-center'  onSubmit={handleFormSubmit}>
            <div className='text-white font-poppins text-2xl tracking-widest'>Login form</div>


           <div>
             <label htmlFor="email">Email</label>
            <span className="text-red-600 "> {`:    ${errEmail}`}</span>
             <input
              onKeyUp={() => {
                handleEmail();
              }}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              type="email"
              className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`}
              id="email"
              placeholder="Your Email"
            />
          </div>
          <div>
            <label htmlFor="password">Password</label>
            <span className="text-red-600 "> {`:    ${errPass}`}</span>

            <input
              onKeyUp={() => {
                handlePass();
              }}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              type="password"
              className={`w-full p-2 text-primary border rounded-md outline-none text-sm transition duration-150 ease-in-out mb-4`}
              id="password"
              placeholder="Your Password"
            />
          </div>

          <div className="flex justify-center items-center mt-6">
            <button
              type="submit"
              className={`bg-green py-2 px-4 text-sm text-black font-bold rounded border border-green focus:outline-none focus:border-green-dark`}
            >
              Login
            </button>
          </div>
        </form>
      </div>





    </div>
  );
}

export default AdminLogin;
